import React from 'react';
import './Sidebar.css';
import Row from 'react-bootstrap/Row';
import Card from 'react-bootstrap/Card';
import ListGroup from 'react-bootstrap/ListGroup';
import Button from 'react-bootstrap/Button';
import Collapse from 'react-bootstrap/Collapse';

export default class Sidebar extends React.Component {
  constructor() {
    super(props);
    this.state = {
      open: false,
      setOpen: false
    }
  }

  render() {
    const [open, setOpen] = this.state;

    return (
      <div className='sidebar'>
        <Row>
          <h1 className="text-center"><a href="index.js">Tasktastic!</a></h1>
        </Row>
        <Row>
          <Card>
            <Card.Header>
              <Button onClick={() => setOpen(!this.state.open)} aria-controls='collapse1' aria-expanded={open}>
                Views
              </Button>
            </Card.Header>
            <Collapse in={open}>
              <div id='collapse1'>
                <ListGroup variant='flush'>
                  <ListGroup.Item>Calendar</ListGroup.Item>
                  <ListGroup.Item>Categories</ListGroup.Item>
                </ListGroup>
              </div>
            </Collapse>
          </Card>
        </Row>
      </div>
    );
  }
}
