import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Collapse from 'react-bootstrap/Collapse';
import ListGroup from 'react-bootstrap/ListGroup';
import Row from 'react-bootstrap/Row';
import './Sidebar.css';

function Sidebar() {
  const [open, setOpen] = useState(false);

  return (
    <div>
      <Row>
        <h1 class='text-center'>Tasktastic</h1>
      </Row>
      <Row>
        <ListGroup className='sidebar'>
          <ListGroup.Item variant='flush' className='title-collapsible'>
            <Button onClick={() => setOpen(!open)} aria-controls='collapse1' aria-expanded={open}>
              Cras justo odio
            </Button>
          </ListGroup.Item>
          <Collapse in={open}>
            <div id='collapse1'>
                <ListGroup.Item className='text-center collapsible'>Dapibus ac facilisis in</ListGroup.Item>
                <ListGroup.Item className='text-center collapsible'>Morbi leo risus</ListGroup.Item>
                <ListGroup.Item className='text-center collapsible'>Porta ac consectetur ac</ListGroup.Item>
                <ListGroup.Item className='text-center collapsible'>Vestibulum at eros</ListGroup.Item>
            </div>
          </Collapse>
        </ListGroup>
      </Row>
    </div>
  )
}

export default Sidebar;
