    else {
        show_all_button.innerHTML = "Show checked checkboxes";
        for (var i = 0; i < checkbox.length; i++) {
            if (checkbox[i].checked) {
                task[i].style.display = 'none';
            }
        }
    }

  }

  render() {
    return (
      <div id='show-all-div'>
        <Button id='show-all' onClick={ () => this.showAll() }>Show all checkboxes</Button>
      </div>
    );
  }
}
