import React from 'react';
import './Sidebar.css';
import Row from 'react-bootstrap/Row';
import Card from 'react-bootstrap/Card';
import ListGroup from 'react-bootstrap/ListGroup';
import Button from 'react-bootstrap/Button';
import Collapse from 'react-bootstrap/Collapse';

export default class Sidebar extends React.Component {

  render() {
    return (
      <div className='sidebar'>
        <Row>
          <h1 className="text-center"><a href="index.js">Tasktastic!</a></h1>
        </Row>
        <Row>
          <Card>
            <Card.Header>
              <Button onClick={() => this.state.onOpen(!this.state.open)} aria-controls='collapse1' aria-expanded={this.state.open}>
                Views
              </Button>
            </Card.Header>
            <Collapse in={this.state.open}>
              <div id='collapse1'>
                <ListGroup variant='flush'>
                  <ListGroup.Item>Calendar</ListGroup.Item>
                  <ListGroup.Item>Categories</ListGroup.Item>
                </ListGroup>
              </div>
            </Collapse>
          </Card>
        </Row>
      </div>
    );
  }
}
