import React from 'react';
import Form from 'react-bootstrap/Form'

export default class Checkbox extends React.Component {
    constructor(props) {
        super(props);
        this.state = { display: 'block', checked: false };
        this.changeDisplay = this.changeDisplay.bind(this);
    }

    componentDidUpdate() {
      if (this.state.checked) {
        this.setState({display: 'none'});
      }
    }

    changeDisplay() {
        const newDisplay = this.state.display === 'block' ? 'none' : 'block';
        const newChecked = this.state.checked === true ? false : true;
        this.setState({ display: newDisplay, checked: newChecked });
    }

    render() {
      return (
        <Form.Check className='checkbox'  checked={this.state.checked} type='checkbox' label={this.props.task} onClick={() => this.changeDisplay()} style={{display: this.state.display}}/>
      );
    }
}
