import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Collapse from 'react-bootstrap/Collapse';
import ListGroup from 'react-bootstrap/ListGroup';
import Row from 'react-bootstrap/Row';
import './Sidebar.css';

function Sidebar() {
  const [open, setOpen] = useState(false);

  return (
    <div className='sidebar' style={{paddingLeft: '20px'}}>
      <Row>
        <h1 class='text-center'>Tasktastic</h1>
      </Row>
      <Row>
        <ListGroup>
          <ListGroup.Item variant='flush' className='text-center sidebar-item' style={{border: 'none'}}>
            <Button style={{width: '100%'}} onClick={() => setOpen(!open)} aria-controls='collapse1' aria-expanded={open}>
              <h3>Views</h3>
            </Button>
          </ListGroup.Item>
          <Collapse in={open}>
            <div id='collapse1'>
                <ListGroup.Item className='text-center sidebar-item' style={{border: 'none'}}><h4>Categories</h4></ListGroup.Item>
                <ListGroup.Item className='text-center sidebar-item' style={{border: 'none'}}><h4>Calendar</h4></ListGroup.Item>
            </div>
          </Collapse>
        </ListGroup>
      </Row>
    </div>
  )
}

export default Sidebar;
