import React from 'react';
import Card from 'react-bootstrap/Card'
import Form from 'react-bootstrap/Form'

export default class IntroToComputerArchitecture extends React.Component {
  render() {
    return (
      <Card style={{backgroundColor: 'var(--innovation-and-design)', border: 'none', padding: '1rem'}}>
        <h4>Intro to Computer Architecture</h4>
          <Form>
            <div key={'default-checkbox'}>
              <Form.Check type='checkbox' label='Homework'/>
              <Form.Check type='checkbox' label='Study for test'/>
              <Form.Check type='checkbox' label='Study for quiz'/>
            </div>
          </Form>
      </Card>
    );
  }
}
