import React from 'react';

export default class Checkbox extends React.Component {
    constructor(props) {
        super(props);
        this.state = { display: 'block' };
        this.changeDisplay = this.changeDisplay.bind(this);
    }

    changeDisplay() {
        const newDisplay = this.state.display === 'block' ? 'none' : 'block';
        this.setState({ display: newDisplay });
    }

    render() {
        return (
            <div class="form-check" style={{display: this.state.display}}>
                <input type="checkbox" onClick={this.changeDisplay} />
                <label class="form-check-label" for="check1">{this.props.task}</label>
            </div>
        );
    }
}
