import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Collapse from 'react-bootstrap/Collapse';
import ListGroup from 'react-bootstrap/ListGroup';
import Row from 'react-bootstrap/Row';
import './Sidebar.css';

function Sidebar() {
  const [open, setOpen] = useState(false);

  return (
    <div className='sidebar' style={{paddingLeft: '20px'}}>
      <Row style={{height: '20%'}}>
        <h1 className='text-center'>Tasktastic</h1>
      </Row>
      <Row>
        <ListGroup>
          <Button style={{width: '100%', backgroundColor: 'var(--primary-color)', border: 'none', color: 'black'}} onClick={() => setOpen(!open)} aria-controls='collapse1' aria-expanded={open}>
            <h3>Views</h3>
          </Button>
          <Collapse in={open}>
            <div id='collapse1' style={{margin: '10px', borderRadius: '10px'}}>
                <ListGroup.Item className='text-center sidebar-item views' style={{border: 'none'}}><h4>Categories</h4></ListGroup.Item>
                <ListGroup.Item className='text-center sidebar-item views' style={{border: 'none'}}><h4>Calendar</h4></ListGroup.Item>
            </div>
          </Collapse>
        </ListGroup>
      </Row>
    </div>
  )
}

export default Sidebar;
